sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   // alert("Thank you for loading the NeedIt form.   When you close this alert you will be able to interact with the form.");

// 	disableField();
	printHeader();
	console.log(getUserInfo(g_user.firstName, g_user.lastName, g_user.userID));
	console.log('The user ' + ((getUserRole()) ? 'can' : 'can\'t') + " manage client scripts\n");
	printFooter();

	try {
		throw new Error('This a custom error thrown');
	} catch (err) {
		jslog("Kenn: " + err.message);
		console.log("Kenn: " + err.message);
	}

}

function disableField() {
	g_form.setDisabled('short_description', true);
}

function getUserInfo() {
	var output = '';

	for(var x in arguments) {
		output += "*** " + arguments[x] + "\n";
	}

	return output;
}

function printHeader() {
	console.log("=======================\n");
	console.log("==  My console logs  ==\n");
	console.log("=======================\n");
}

function printFooter() {
	console.log("=======================\n");
}

function getUserRole() {
	return g_user.hasRoleExactly('client_script_admin');
}
